mapper.select:
SELECT * FROM ( SELECT SUM(1) OVER(PARTITION BY NULL) AS TOTAL_RECORD_COUNT, ROW_NUMBER() OVER (ORDER BY ROWNUM DESC) AS RNUM, B.CENTER_NM, C.FLOOR_NM, TB.TICKET_MCHN_SNO,TB.TICKET_MCHN_REMARK, CASE TB.USE_YN WHEN 'Y' THEN '사용' ELSE '사용 안함' END AS USE_YN, TB.LAST_UPDT_DTM,TB.LAST_UPDUSR_ID FROM TSEB_CENTER_INFO_M B, TSEB_FLOOR_INFO_M C, ( SELECT A.TICKET_MCHN_SNO, A.CENTER_CD, A.FLOOR_CD, A.TICKET_MCHN_REMARK, A.USE_YN, TO_CHAR(A.LAST_UPDT_DTM, 'yyyy-mm-dd') AS LAST_UPDT_DTM, A.LAST_UPDUSR_ID FROM TSEC_TICKET_MCHN_M A WHERE 1=1 ) TB WHERE B.CENTER_CD = TB.CENTER_CD AND C.FLOOR_CD = TB.FLOOR_CD
AND B.CENTER_NM LIKE '%' || #{params.searchKeyword} || '%'
AND C.FLOOR_NM LIKE '%' || #{params.searchKeyword} || '%'
AND ( B.CENTER_NM LIKE '%' || #{params.searchKeyword} || '%' OR C.FLOOR_NM LIKE '%' || #{params.searchKeyword} || '%' )
AND B.CENTER_CD = #{params.searchCenterCd}
) TA WHERE RNUM BETWEEN #{params.firstIndex} + 1 AND #{params.firstIndex} + #{params.recordCountPerPage} ORDER BY TA.TICKET_MCHN_SNO DESC

SELECT  TICKET_MCHN_SNO, CENTER_CD, FLOOR_CD, USE_YN, TICKET_MCHN_REMARK
FROM TSEC_TICKET_MCHN_M
WHERE TICKET_MCHN_SNO = #{ticketMchnSno}
