mapper.select:
SELECT * FROM ( SELECT SUM(1) OVER(PARTITION BY NULL) AS TOTAL_RECORD_COUNT, ROW_NUMBER() OVER (ORDER BY ROWNUM DESC) AS RNUM, TB.* FROM ( SELECT A.AUTHOR_CODE, A.AUTHOR_NM, A.AUTHOR_DC, TO_CHAR(A.AUTHOR_CREAT_DE, 'yyyy-mm-dd') AS AUTHOR_CREAT_DE FROM COMTNAUTHORINFO A WHERE 1=1
AND ( A.AUTHOR_CODE LIKE '%' || #{params.searchKeyword} || '%' OR A.AUTHOR_NM LIKE '%' || #{params.searchKeyword} || '%' )
) TB ) TA WHERE ROWNUM BETWEEN #{params.firstIndex} + 1 AND #{params.firstIndex} + #{params.recordCountPerPage} ORDER BY TA.RNUM DESC

mapper.update:
		UPDATE COMTNAUTHORINFO SET AUTHOR_NM = #{authorNm, jdbcType=VARCHAR}
		                           , AUTHOR_DC = #{authorDc, jdbcType=VARCHAR}                        
       	WHERE AUTHOR_CODE = #{authorCode}

mapper.delete:
   	    {CALL
	        DECLARE
	        BEGIN
	            DELETE FROM COMTNAUTHORINFO WHERE AUTHOR_CODE = #{authorCode};
	            DELETE FROM COMTNMENUCREATDTLS  WHERE AUTHOR_CODE= #{authorCode};
	        END
	    }
