mapper.select:
SELECT * FROM ( SELECT SUM(1) OVER(PARTITION BY NULL) AS TOTAL_RECORD_COUNT, ROW_NUMBER() OVER (ORDER BY ROWNUM DESC) AS RNUM, TB.* FROM ( SELECT A.PROGRM_FILE_NM, A.PROGRM_STRE_PATH, A.PROGRM_KOREANNM, A.PROGRM_DC, A.URL FROM COMTNPROGRMLIST A WHERE 1=1
AND ( A.PROGRM_FILE_NM LIKE '%'|| #{params.searchKeyword} || '%' OR A.PROGRM_KOREANNM LIKE '%' || #{params.searchKeyword} || '%' )
) TB ) TA WHERE ROWNUM BETWEEN #{params.firstIndex} + 1 AND #{params.firstIndex} + #{params.recordCountPerPage} ORDER BY TA.RNUM DESC

SELECT A.PROGRM_FILE_NM, A.PROGRM_STRE_PATH, A.PROGRM_KOREANNM, A.PROGRM_DC, A.URL
FROM COMTNPROGRMLIST A
WHERE A.PROGRM_FILE_NM = #{progrmFileNm}

mapper.insert:
INSERT INTO COMTNPROGRMLIST	
(
    PROGRM_FILE_NM, PROGRM_STRE_PATH, PROGRM_KOREANNM, PROGRM_DC, URL
)
VALUES 
(
	#{progrmFileNm}, #{progrmStrePath,jdbcType=VARCHAR}, #{progrmKoreannm,jdbcType=VARCHAR}, #{progrmDc,jdbcType=VARCHAR}, #{url,jdbcType=VARCHAR}	
)

