mapper.update:
	UPDATE TSEB_CENTERHOLY_INFO_I A
	SET (A.HOLY_DT, A.HOLY_NM, A.USE_YN, A.LAST_UPDUSR_ID, A.LAST_UPDT_DTM) = 
   	(
   		SELECT B.HOLY_DT, B.HOLY_NM, B.USE_YN, B.LAST_UPDUSR_ID, SYSDATE
		FROM TSEB_NOSHOW_INFO_M B 
		WHERE A.NOSHOW_DAY = B.NOSHOW_DAY 
		AND B.CENTER_CD = #{params.copyCenterCd}
	)
	WHERE A.CENTER_CD = #{params.targetCenterCd}

mapper.select:
SELECT CENTER_HOLY_SEQ, CENTER_CD, HOLY_DT, HOLY_NM, USE_YN, LAST_UPDUSR_ID
FROM TSEB_CENTERHOLY_INFO_I
WHERE CENTER_HOLY_SEQ = #{centerHolySeq}
