mapper.select:
SELECT TB.* FROM ( SELECT SUM(1) OVER(PARTITION BY NULL) AS TOTAL_RECORD_COUNT, ROW_NUMBER() OVER (ORDER BY A.FLOOR_CD DESC) AS RNUM, A.FLOOR_CD, A.FLOOR_NM, A.FLOOR_INFO, B.CENTER_CD, B.CENTER_NM, CASE WHEN LENGTH(FLOOR_MAP1) > 0 THEN FLOOR_MAP1 ELSE 'no_image.png' END FLOOR_MAP1, FN_DETAILCODENM(A.FLOOR_INFO) AS FLOOR_INFO_TXT, A.FLOOR_SEAT_CNT, A.USE_YN, A.FLOOR_PART_DVSN, TO_CHAR(A.LAST_UPDT_DTM, 'yyyy-mm-dd') AS LAST_UPDT_DTM, A.LAST_UPDUSR_ID FROM TSEB_FLOOR_INFO_M A, TSEB_CENTER_INFO_M B WHERE A.CENTER_CD = B.CENTER_CD
AND ( B.CENTER_NM LIKE '%' || #{params.searchKeyword} || '%' )
AND B.CENTER_CD = #{params.centerCd}
AND A.USE_YN = #{params.useYn}
AND A.FLOOR_CD = #{params.floorCd}
) TB WHERE RNUM BETWEEN #{params.firstIndex} + 1 AND #{params.firstIndex} + #{params.recordCountPerPage} ORDER BY RNUM ASC

mapper.update:
UPDATE TSEB_FLOOR_INFO_M SET  
	FLOOR_NM = #{floorNm, jdbcType=VARCHAR}, 
	USE_YN = #{useYn, jdbcType=VARCHAR}, 
	FLOOR_PART_DVSN = #{floorPartDvsn, jdbcType=VARCHAR},
	FLOOR_SEAT_RULE = #{floorSeatRule, jdbcType=VARCHAR}  

		,FLOOR_MAP1 = #{floorMap1, jdbcType=VARCHAR}


		,FLOOR_MAP2 = #{floorMap2, jdbcType=VARCHAR}

	,LAST_UPDT_DTM = SYSDATE
	,LAST_UPDUSR_ID = #{lastUpdusrId, jdbcType=VARCHAR}
WHERE FLOOR_CD = #{floorCd}
