mapper.select:
SELECT TB.* FROM ( SELECT SUM(1) OVER(PARTITION BY NULL) AS TOTAL_RECORD_COUNT, ROW_NUMBER() OVER (ORDER BY C.PART_ORDER) AS RNUM, C.PART_CD, C.PART_NM, B.FLOOR_CD, B.FLOOR_INFO, B.FLOOR_NM, A.CENTER_CD, A.CENTER_NM, CASE WHEN LENGTH(PART_MAP1) > 0 THEN PART_MAP1 ELSE 'no_image.png' END PART_MAP1, B.FLOOR_PART_DVSN, C.LAST_UPDUSR_ID, TO_CHAR(C.LAST_UPDT_DTM, 'yyyy-mm-dd') AS LAST_UPDT_DTM, C.PART_SEAT_RULE, C.PART_MINI_CSS, C.PART_MINI_TOP, C.PART_MINI_LEFT, C.PART_MINI_WIDTH, C.PART_MINI_HEIGHT, C.PART_MINI_ROTATE, C.USE_YN, C.PART_ORDER, C.PART_CSS, NVL((SELECT COUNT(*) FROM TSEB_SEAT_INFO_D WHERE PART_CD = C.PART_CD), 0) AS SEAT_CNT, D.PART_CLASS_SEQ, FN_DETAILCODENM(D.PART_CLASS) AS PART_CLASS_TEXT FROM TSEB_CENTER_INFO_M A, TSEB_FLOOR_INFO_M B, TSEB_PART_INFO_D C, TSEB_CENTER_PART_CLASS_D D WHERE A.CENTER_CD = B.CENTER_CD AND C.FLOOR_CD = #{params.floorCd} AND B.FLOOR_CD = C.FLOOR_CD AND C.PART_CLASS_SEQ = D.PART_CLASS_SEQ
AND ( B.FLOOR_NM LIKE '%' || #{params.searchKeyword} || '%' OR A.CENTER_NM LIKE '%' || #{params.searchKeyword} || '%' )
AND B.FLOOR_CD = #{params.floorCd}
AND B.CENTER_CD = #{params.centerCd}
AND C.USE_YN = #{params.useYn}
) TB WHERE RNUM BETWEEN #{params.firstIndex} + 1 AND #{params.firstIndex} + #{params.recordCountPerPage} ORDER BY RNUM

SELECT PART_CD, PART_NM, FLOOR_CD FROM TSEB_PART_INFO_D WHERE USE_YN = 'Y'
AND FLOOR_CD = #{floorCd, jdbcType=VARCHAR}
ORDER BY PART_CD ASC

mapper.update:
UPDATE TSEB_PART_INFO_D SET PART_NM = #{partNm},
PART_MAP1 = #{partMap1, jdbcType=VARCHAR},
PART_CLASS = #{partClass}, PART_CLASS_SEQ = #{partClassSeq}, PART_ORDER = #{partOrder, jdbcType=VARCHAR}, USE_YN = #{useYn}, LAST_UPDT_DTM = SYSDATE, LAST_UPDUSR_ID = #{lastUpdusrId, jdbcType=VARCHAR} WHERE PART_CD = #{partCd}
