mapper.select:
SELECT * FROM ( SELECT SUM(1) OVER(PARTITION BY NULL) AS TOTAL_RECORD_COUNT, ROW_NUMBER() OVER (ORDER BY ROWNUM DESC) AS RNUM, TB.* FROM ( SELECT a.ADMIN_ID, a.EMP_NO, b.EMP_NM, b.EMP_CLPHN, b.EMP_TLPHN, b.EMP_EMAIL, FN_ORGNM(b.DEPT_CD, 'DEPT') DEPT_NM, a.ADMIN_LOCKYN, a.USE_YN, TO_CHAR(a.LAST_UPDT_DTM, 'YYYY-MM-DD') LAST_UPDT_DTM, a.AUTHOR_CD, c.AUTHOR_NM, a.CENTER_CD, CASE WHEN ( AUTHOR_CD != 'ROLE_ADMIN' OR AUTHOR_CD != 'ROLE_SYSTEM') THEN (SELECT CENTER_NM FROM TSEB_CENTER_INFO_M WHERE CENTER_CD = A.CENTER_CD) ELSE '' END ADMIN_DVSN FROM TSEH_ADMIN_INFO_M a , TSEH_EMP_INFO_M b, COMTNAUTHORINFO c WHERE a.EMP_NO = b.EMP_NO AND a.AUTHOR_CD = c.AUTHOR_CODE
AND a.ADMIN_ID LIKE '%'|| #{params.searchKeyword}|| '%'
AND b.EMP_NM LIKE '%'|| #{params.searchKeyword}|| '%'
AND (a.ADMIN_ID LIKE '%'|| #{params.searchKeyword}|| '%' OR b.EMP_NM LIKE '%'|| #{params.searchKeyword}|| '%' )
AND a.AUTHOR_CD = #{params.searchAuthorCd}
AND b.DEPT_CD = #{params.searchDeptCd}
AND A.CENTER_CD = #{params.searchCenter}
) TB ) TA WHERE RNUM BETWEEN #{params.firstIndex} + 1 AND #{params.firstIndex} + #{params.recordCountPerPage} ORDER BY TA.LAST_UPDT_DTM DESC

mapper.insert:
   {CALL
			DECLARE
			BEGIN     

                SELECT NVL(COUNT(EMP_NO),0)  as cnt
                FROM TSEH_ADMIN_INFO_M 
                WHERE ADMIN_ID = #{adminId}



                       UPDATE TSEH_ADMIN_INFO_M SET USE_YN = #{useYn}
	                               ,  LAST_UPDUSR_ID = #{lastUpdusrId, jdbcType=VARCHAR }
	                               ,  LAST_UPDT_DTM = SYSDATE
	                               ,  AUTHOR_CD = #{authorCd , jdbcType=VARCHAR }
	                               ,  CENTER_CD = #{centerCd , jdbcType=VARCHAR }
		                           , ADMIN_LOCKYN ='N'

		                            ,  EMP_NO = #{empNo, jdbcType=VARCHAR }

	                   WHERE ADMIN_ID = #{adminId};


				       INSERT INTO TSEH_ADMIN_INFO_M(ADMIN_ID, EMP_NO, AUTHOR_CD, ADMIN_LOCKYN, USE_YN, ADMIN_PASS_UPDT_DT,
				                                     CENTER_CD, FRST_REGIST_DTM, FRST_REGTER_ID, LAST_UPDT_DTM, LAST_UPDUSR_ID)
				       VALUES (	#{adminId} , #{empNo}, #{authorCd}, 'N', 'Y', SYSDATE,
				                #{centerCd ,jdbcType=VARCHAR}, SYSDATE, #{frstRegterId ,jdbcType=VARCHAR}, SYSDATE, #{lastUpdusrId ,jdbcType=VARCHAR} );

				       UPDATE TSEH_EMP_INFO_M SET ADMIN_DVSN = 'Y' WHERE EMP_NO = #{empNo};



            END
          }

mapper.update:
   UPDATE TSEH_ADMIN_INFO_M SET USE_YN = #{useYn}
                               ,  LAST_UPDUSR_ID = #{lastUpdusrId, jdbcType=VARCHAR }
                               ,  LAST_UPDT_DTM = SYSDATE
                               ,  AUTHOR_CD = #{authorCd , jdbcType=VARCHAR }
                               ,  CENTER_CD = #{centerCd , jdbcType=VARCHAR }
	                           , ADMIN_LOCKYN ='N'

	                            ,  EMP_NO = #{empNo, jdbcType=VARCHAR }

   WHERE ADMIN_ID = #{adminId}
