mapper.select:
SELECT * FROM ( SELECT SUM(1) OVER(PARTITION BY NULL) AS TOTAL_RECORD_COUNT, ROW_NUMBER() OVER (ORDER BY ROWNUM DESC) AS RNUM, TB.* FROM ( SELECT A.PSIT_CD code_cd, A.PSIT_NM code_nm, A.PSIT_DC code_dc, A.USE_YN, TO_CHAR(A.LAST_UPDT_DTM, 'yyyy-mm-dd') AS LAST_UPDT_DTM FROM TSEH_PSIT_INFO_M A WHERE 1=1
AND ( A.PSIT_NM LIKE CONCAT('%',#{params.searchKeyword},'%') OR A.PSIT_DC LIKE CONCAT('%',#{params.searchKeyword},'%') )
) TB ) TA WHERE ROWNUM BETWEEN #{params.firstIndex} + 1 AND #{params.firstIndex} + #{params.recordCountPerPage} ORDER BY TA.RNUM DESC

SELECT A.PSIT_CD code_cd, A.PSIT_NM code_nm, A.PSIT_DC code_dc, A.USE_YN, A.LAST_UPDT_DTM
FROM TSEH_PSIT_INFO_M A
WHERE A.PSIT_CD = #{code}

mapper.update:
		UPDATE TSEH_PSIT_INFO_M SET PSIT_NM = #{psitNm, jdbcType=VARCHAR}
		                           , PSIT_DC = #{psitDc, jdbcType=VARCHAR}    
		                           , USE_YN = #{useYn, jdbcType=VARCHAR} 
		                           , LAST_UPDT_DTM = SYSDATE            
       	WHERE PSIT_CD = #{psitCd}
