mapper.select:
SELECT * FROM ( SELECT SUM(1) OVER(PARTITION BY NULL) AS TOTAL_RECORD_COUNT, ROW_NUMBER() OVER (ORDER BY TB.RESV_SEQ DESC) AS RNUM, TB.* FROM ( SELECT A.RESV_SEQ, A.SEAT_CD, A.SEASON_CD, A.RESV_START_DT, A.RESV_END_DT, A.RESV_DVSN, A.RESV_START_TM, A.RESV_END_TM, TO_CHAR(A.RESV_REQ_DATE,'YYYY-MM-DD HH24:MI') AS RESV_REQ_DATE, A.USER_ID, A.RESV_USER_DVSN, A.RESV_USER_ASK_YN, A.RESV_RCPT_YN, A.RESV_RCPT_NUMBER, A.RESV_PAY_COST, A.RESV_PAY_DVSN, A.RESV_TICKET_DVSN, A.RESV_STATE, FN_DETAILCODENM(A.RESV_TICKET_DVSN) AS RESV_TICKET_DVSN_TEXT, FN_DETAILCODENM(A.RESV_PAY_DVSN) AS RESV_PAY_DVSN_TEXT, FN_DETAILCODENM(A.RESV_STATE) AS RESV_STATE_TEXT, FN_DETAILCODENM(E.PART_CLASS) AS PART_CLASS, CASE WHEN A.RESV_ENTRY_DVSN = 'ENTRY_DVSN_1' THEN '자유석' ELSE (SELECT SEAT_NM FROM TSEB_SEAT_INFO_D WHERE SEAT_CD = A.SEAT_CD) END AS SEAT_NM, C.CENTER_NM, C.CENTER_PILOT_YN, D.USER_NM, REGEXP_REPLACE(D.USER_PHONE, '(02|.{3})(.+)(.{4})', '\1-\2-\3') AS USER_PHONE FROM TSER_RESV_INFO_I A, TSEB_SEAT_INFO_D B, TSEB_CENTER_INFO_M C, TSER_USER_INFO_I D, TSEB_PART_INFO_D E WHERE A.SEAT_CD = B.SEAT_CD(+) AND A.PART_CD = E.PART_CD(+) AND A.CENTER_CD = C.CENTER_CD AND A.USER_ID = D.USER_ID
AND A.CENTER_CD = #{params.searchCenterCd}
AND A.RESV_END_DT BETWEEN #{params.searchFrom} AND #{params.searchTo}
AND TO_CHAR(TO_DATE(A.RESV_REQ_DATE,'YYYY-MM-DD HH24:MI'),'YYYY-MM-DD HH24:MI') BETWEEN #{params.searchFrom} AND #{params.searchTo}
AND A.RESV_SEQ LIKE '%' || #{params.searchKeyword} || '%'
AND A.RESV_USER_NM LIKE '%' || #{params.searchKeyword} || '%'
AND A.USER_ID LIKE '%' || #{params.searchKeyword} || '%'
AND A.RESV_USER_CLPHN LIKE '%' || #{params.searchKeyword} || '%'
AND ( A.RESV_SEQ LIKE '%' || #{params.searchKeyword} || '%' OR A.RESV_USER_NM LIKE '%' || #{params.searchKeyword} || '%' OR A.USER_ID LIKE '%' || #{params.searchKeyword} || '%' )
AND A.CENTER_CD = #{params.centerCd}
AND A.RESV_STATE != #{params.searchResvState}
AND A.RESV_STATE = #{params.searchResvState}
AND A.RESV_PAY_DVSN = #{params.searchResvPayDvsn}
) TB ORDER BY TB.RESV_SEQ DESC ) TA WHERE RNUM BETWEEN #{params.firstIndex} + 1 AND #{params.firstIndex} + #{params.recordCountPerPage} ORDER BY TA.RESV_SEQ DESC

SELECT A.RESV_SEQ, A.RESV_USER_NM, B.CENTER_NM, A.RESV_TICKET_DVSN, CASE WHEN A.RESV_ENTRY_DVSN = 'ENTRY_DVSN_1' THEN '자유석' ELSE (SELECT SEAT_NM FROM TSEB_SEAT_INFO_D WHERE SEAT_CD = A.SEAT_CD) END AS SEAT_NM, A.RESV_START_DT, A.RESV_END_DT, A.RESV_STATE, FN_DETAILCODENM(A.RESV_STATE) AS RESV_STATE_TEXT, TO_CHAR(A.RESV_REQ_DATE,'YYYY-MM-DD HH24:MI') AS RESV_REQ_DATE, A.RESV_PAY_DVSN FROM TSER_RESV_INFO_I A, TSEB_CENTER_INFO_M B WHERE A.CENTER_CD = B.CENTER_CD AND A.USER_ID = #{params.userId}
AND A.RESV_STATE NOT IN ('RESV_STATE_4')
AND A.RESV_STATE = 'RESV_STATE_4'
AND TO_CHAR(A.RESV_REQ_DATE,'YYYYMMDD') BETWEEN #{params.searchDayFrom} AND #{params.searchDayTo}
AND A.RESV_END_DT BETWEEN #{params.searchDayFrom} AND #{params.searchDayTo}
ORDER BY A.RESV_REQ_DATE DESC

SELECT NVL(COUNT(*),0) AS CNT
FROM TSER_RESV_INFO_I A


		WHERE (A.USER_ID = #{params.userId} OR A.RESV_USER_CLPHN = #{params.userPhone}) 


		WHERE A.RESV_USER_CLPHN = #{params.userPhone}


AND A.RESV_END_DT = #{params.resvDate}
AND A.RESV_STATE NOT IN ('RESV_STATE_4')


SELECT A.RESV_DATE FROM 
(
	SELECT TO_CHAR(TO_DATE(#{resvDateFrom},'YYYYMMDD') + (LEVEL-1), 'YYYYMMDD') AS RESV_DATE 
	FROM DUAL
	CONNECT BY TO_CHAR(TO_DATE(#{resvDateFrom},'YYYYMMDD') + (LEVEL-1), 'YYYYMMDD') <= #{resvDateTo}
) A
WHERE A.RESV_DATE NOT IN (
	SELECT HOLY_DT FROM TSEB_CENTERHOLY_INFO_I 
	WHERE CENTER_CD = #{centerCd}
)
ORDER BY A.RESV_DATE ASC

mapper.update:
		UPDATE TSER_RESV_INFO_I SET 
			RESV_STATE = #{resvState}
        WHERE RESV_SEQ = #{resvSeq}

		UPDATE TSER_RESV_INFO_I SET 	
			RESV_QR_COUNT = RESV_QR_COUNT + 1
        WHERE RESV_SEQ = #{resvSeq}

		UPDATE TSER_RESV_INFO_I SET 
			RESV_RCPT_NUMBER = #{resvRcptNumber}
        WHERE RESV_SEQ = #{resvSeq}

mapper.select:
SELECT count(*) AS CNT
	FROM TSEC_TICKET_MCHN_M  
WHERE TICKET_MCHN_SNO = #{params.ticketMchnSno}
AND CENTER_CD = #{params.centerCd}
