mapper.select:
SELECT A.RESV_DATE, B.CENTER_NM, A.SEAT_ALL_COUNT, A.M_RESV_ALL_COUNT, A.M_RESV_SUCCESS_COUNT, A.M_RESV_CANCEL_COUNT, A.M_RESV_SUCCESS_PER || '%' AS M_RESV_SUCCESS_PER, A.G_RESV_ALL_COUNT, A.G_RESV_SUCCESS_COUNT, A.G_RESV_CANCEL_COUNT, A.G_RESV_SUCCESS_PER || '%' AS G_RESV_SUCCESS_PER, A.T_RESV_ALL_COUNT, A.T_RESV_SUCCESS_COUNT, A.T_RESV_CANCEL_COUNT, A.T_RESV_SUCCESS_PER || '%' AS T_RESV_SUCCESS_PER, A.T_RESV_CANCEL_PER || '%' AS T_RESV_CANCEL_PER, A.T_CENTER_USE_PER || '%' AS T_CENTER_USE_PER FROM TSET_CENTER_RESV_STAT_M A, TSEB_CENTER_INFO_M B WHERE A.CENTER_CD = B.CENTER_CD
AND A.CENTER_CD = #{params.searchCenterCd}
AND A.RESV_DATE = #{params.searchResvDateFrom}
