mapper.select:
SELECT * FROM ( SELECT SUM(1) OVER(PARTITION BY NULL) AS TOTAL_RECORD_COUNT, ROW_NUMBER() OVER (ORDER BY BOARD_GROUP DESC) AS RNUM, TB.* FROM ( SELECT LPAD(' ', 2*(LEVEL-1)) || BOARD_TITLE AS BOARD_TITLE, BOARD_SEQ, BOARD_CD, BOARD_REFNO, BOARD_CLEVEL, BOARD_NOTICE_START_DAY, BOARD_NOTICE_END_DAY, BOARD_POPUP, BOARD_TOP_SEQ, BOARD_VISIT_CNT, USE_YN, LAST_UPDUSR_ID, BOARD_GROUP, TO_CHAR(LAST_UPDT_DTM, 'yyyy-MM-dd') LAST_UPDT_DTM, BOARD_ALL_NOTICE FROM TSES_BOARD_INFO_I a WHERE a.BOARD_CD = #{params.boardCd}
AND TO_CHAR(SYSDATE, 'yyyyMMdd') BETWEEN NVL(a.BOARD_NOTICE_START_DAY, '20211201') AND NVL(a.BOARD_NOTICE_END_DAY , '20991231')
AND ( BOARD_TITLE LIKE '%'||#{params.searchKeyword } ||'%' OR BOARD_CD LIKE '%'||#{params.searchKeyword } ||'%' )
AND #{params.centerCd} IN (SELECT COLUMN_VALUE FROM TABLE(UF_SPLICT(a.BOARD_CENTER_ID, ',')) )
AND #{params.searchCenterCd} IN (SELECT COLUMN_VALUE FROM TABLE(UF_SPLICT(a.BOARD_CENTER_ID, ',')) )
START WITH BOARD_REFNO = 0 CONNECT BY PRIOR BOARD_SEQ = BOARD_REFNO ORDER SIBLINGS BY BOARD_GROUP DESC ) TB )TA WHERE RNUM BETWEEN #{params.firstIndex} + 1 AND #{params.firstIndex} + #{params.recordCountPerPage}

mapper.update:
UPDATE TSES_BOARD_INFO_I SET 
	BOARD_VISIT_CNT = BOARD_VISIT_CNT + 1
WHERE BOARD_SEQ = #{boardSeq}

mapper.select:
SELECT STRE_FILE_NM
FROM COMTNFILEDTAIL
WHERE FILE_SEQ = #{boardSeq}

SELECT ORIGNL_FILE_NM 
FROM COMTNFILEDTAIL
WHERE STRE_FILE_NM = #{boardSeq}
