mapper.insert:
{CALL DECLARE BEGIN
SELECT DECODE( NVL(count(MENU_NO), 0), 0, 7001, MAX(MENU_NO) + 1) FROM COMTNMENUINFO WHERE UPPER_MENU_NO = '7000'
INSERT INTO TSES_BRDSET_INFO_M ( BOARD_CD, BOARD_TITLE, BOARD_DVSN, BOARD_AUTHOR, BOARD_CENTER_ID, BOARD_SIZE, BOARD_NOTICE_DVSN, USE_YN, BOARD_FILE_UPLOAD_YN, BOARD_CMNT_USE, FRST_REGTER_ID, FRST_REGIST_DTM, LAST_UPDUSR_ID, LAST_UPDT_DTM ) VALUES ( #{boardCd}, #{boardTitle}, #{boardDvsn,jdbcType=VARCHAR}, #{boardAuthor,jdbcType=VARCHAR}, #{boardCenterId,jdbcType=VARCHAR}, #{boardSize,jdbcType=VARCHAR}, #{boardNoticeDvsn,jdbcType=VARCHAR}, #{useYn,jdbcType=VARCHAR}, #{boardFileUploadYn,jdbcType=VARCHAR}, #{boardCmntUse,jdbcType=VARCHAR}, #{userId}, sysdate, #{userId}, sysdate );
INSERT INTO COMTNPROGRMLIST ( PROGRM_FILE_NM, PROGRM_STRE_PATH, PROGRM_KOREANNM, PROGRM_DC, URL ) SELECT #{boardCd}, '/backoffice/sys', #{boardTitle}, #{boardTitle,jdbcType=VARCHAR}, '/backoffice/sys/boardList.do?boardCd='|| #{boardCd} FROM DUAL;
INSERT INTO COMTNMENUINFO ( MENU_NO , MENU_ORDR , MENU_NM , UPPER_MENU_NO, MENU_DC, PROGRM_FILE_NM ) SELECT #{menuNo}, #{menuNo}, #{boardTitle}, '7000', #{boardTitle}, #{boardCd} FROM DUAL;
END }

mapper.update:
    {CALL
        DECLARE
        BEGIN
				UPDATE TSES_BRDSET_INFO_M SET BOARD_TITLE = #{boardTitle, jdbcType=VARCHAR}
				                              , BOARD_DVSN = #{boardDvsn, jdbcType=VARCHAR}     
				                              , BOARD_AUTHOR = #{boardAuthor, jdbcType=VARCHAR}     
				                              , BOARD_CENTER_ID = #{boardCenterId, jdbcType=VARCHAR} 
				                              , BOARD_NOTICE_DVSN = #{boardNoticeDvsn, jdbcType=VARCHAR}     
				                              , BOARD_SIZE = #{boardSize, jdbcType=VARCHAR}     
				                              , USE_YN = #{useYn, jdbcType=VARCHAR}     
				                              , BOARD_FILE_UPLOAD_YN = #{boardFileUploadYn, jdbcType=VARCHAR}     
				                              , BOARD_CMNT_USE = #{boardCmntUse, jdbcType=VARCHAR}     
				                              , LAST_UPDUSR_ID = #{userId}   
				                              , LAST_UPDT_DTM = sysdate                     
		       	WHERE BOARD_CD = #{boardCd};

		       	UPDATE COMTNPROGRMLIST SET PROGRM_KOREANNM = #{boardTitle, jdbcType=VARCHAR},
		       	                           PROGRM_DC = #{boardTitle, jdbcType=VARCHAR}
		       	WHERE  PROGRM_FILE_NM = #{boardCd};


		       	UPDATE COMTNMENUINFO SET MENU_NM = #{boardTitle, jdbcType=VARCHAR},
		       	                         MENU_DC = #{boardTitle, jdbcType=VARCHAR}
		       	WHERE PROGRM_FILE_NM = #{boardCd};
		 END
   }

delete.foreach:

   	            DELETE FROM COMTNMENUINFO WHERE PROGRM_FILE_NM = #{item};
   	            DELETE FROM COMTNPROGRMLIST WHERE PROGRM_FILE_NM = #{item};
   	            DELETE FROM TSES_CMNT_INFO_I WHERE BOARD_SEQ IN (SELECT BOARD_SEQ FROM TSES_BOARD_INFO_I WHERE BOARD_CD= #{item});
	            DELETE FROM TSES_BRDSET_INFO_M WHERE BOARD_CD = #{item};
	            DELETE FROM TSES_BOARD_INFO_I  WHERE BOARD_CD= #{item}
