mapper.insert:
INSERT INTO COMTNSYSLOG ( REQUST_ID
	, SRVC_NM
	, METHOD_NM
	, PROCESS_SE_CODE
	, PROCESS_TIME
	, RQESTER_ID
	, RQESTER_IP
	, OCCRRNC_DE
	, OCCRRNC_DATE
	, SQL_PARAM
	, METHOD_RESULT
) VALUES ( #{requstId}
	, #{srvcNm}
	, #{methodNm}
	, #{processSeCode}
	, #{processTime}
	, #{rqesterId}
	, #{rqesterIp}
	, SYSDATE
	, SYSDATE
	, #{sqlParam}
	, #{methodResult}
)

mapper.select:
SELECT * FROM ( SELECT SUM(1) OVER(PARTITION BY NULL) AS TOTAL_RECORD_COUNT, ROW_NUMBER() OVER (ORDER BY ROWNUM DESC) AS RNUM, TB.* FROM ( SELECT a.REQUST_ID , a.SRVC_NM , a.METHOD_NM , CASE a.PROCESS_SE_CODE WHEN 'S' THEN '조회' WHEN 'I' THEN '입력' WHEN 'U' THEN '업데이트' WHEN 'D' THEN '삭제' END PROCESS_SE_CODE , a.PROCESS_TIME , a.RQESTER_IP , a.RQESTER_ID , b.EMP_NM AS RQESTER_NM , a.SQL_PARAM , a.METHOD_RESULT , TO_CHAR(OCCRRNC_DATE, 'YYYY-MM-DD HH24:MI:SS') OCCRRNC_DATE , a.ERROR_SE , a.ERROR_CODE FROM COMTNSYSLOG a, VW_EMPINFO b WHERE a.RQESTER_ID = b.ADMIN_ID
AND a.METHOD_NM LIKE '%' || #{params.searchKeyword} || '%'
AND TO_CHAR(OCCRRNC_DATE, 'yyyymmdd') BETWEEN #{params.searchFrom} AND #{params.searchTo}
ORDER BY a.OCCRRNC_DE DESC ) TB ) WHERE RNUM BETWEEN #{params.firstIndex} + 1 AND #{params.firstIndex} + #{params.recordCountPerPage}
