mapper.select:
SELECT * FROM ( SELECT SUM(1) OVER(PARTITION BY NULL) AS TOTAL_RECORD_COUNT, ROW_NUMBER() OVER (ORDER BY ROWNUM DESC) AS RNUM, TB.* FROM ( SELECT A.PROGRM_FILE_NM, A.PROGRM_KOREANNM, A.URL, A.PROGRM_DC, TO_CHAR(B.PROCESS_DE, 'yyyy-mm-dd') AS PROCESS_DE, OPTER_ID FROM COMTNPROGRMLIST A, COMTHPROGRMCHANGEDTLS B WHERE 1=1
AND ( A.PROGRM_FILE_NM LIKE CONCAT('%',#{params.searchKeyword},'%') OR A.PROGRM_KOREANNM LIKE CONCAT('%',#{params.searchKeyword},'%') )
) TB ) TA WHERE ROWNUM BETWEEN #{params.firstIndex} + 1 AND #{params.firstIndex} + #{params.recordCountPerPage} ORDER BY TA.RNUM DESC

mapper.update:
		UPDATE COMTNAUTHORINFO SET 
			AUTHOR_CODE = #{authorCode}, AUTHOR_NM = #{authorNm, jdbcType=VARCHAR}, AUTHOR_DC = #{authorDc, jdbcType=VARCHAR}                        
       	WHERE AUTHOR_CODE = #{authorCode}
