mapper.select:
SELECT * FROM ( SELECT SUM(1) OVER(PARTITION BY NULL) AS TOTAL_RECORD_COUNT, ROW_NUMBER() OVER (ORDER BY ROWNUM DESC) AS RNUM, TB.* FROM ( SELECT A.DEPT_CD code_cd, A.DEPT_NM code_nm, A.DEPT_DC code_dc, A.USE_YN, TO_CHAR(A.LAST_UPDT_DTM, 'yyyy-mm-dd') AS LAST_UPDT_DTM FROM TSEH_DEPT_INFO_M A WHERE 1=1
AND ( A.DEPT_DC LIKE CONCAT('%',#{params.searchKeyword},'%') OR A.DEPT_NM LIKE CONCAT('%',#{params.searchKeyword},'%') )
) TB ) TA WHERE ROWNUM BETWEEN #{params.firstIndex} + 1 AND #{params.firstIndex} + #{params.recordCountPerPage} ORDER BY TA.RNUM DESC

SELECT A.DEPT_CD code_cd, A.DEPT_NM code_nm, A.DEPT_DC  code_dc, A.USE_YN, A.LAST_UPDT_DTM
FROM TSEH_DEPT_INFO_M A
WHERE A.DEPT_CD = #{code}

mapper.update:
		UPDATE TSEH_DEPT_INFO_M SET DEPT_NM = #{deptNm}
		                           , DEPT_DC = #{deptDc, jdbcType=VARCHAR}    
		                           , USE_YN = #{useYn, jdbcType=VARCHAR} 
		                           , LAST_UPDT_DTM = SYSDATE            
       	WHERE DEPT_CD = #{deptCd}
