mapper.select:
SELECT * FROM ( SELECT SUM(1) OVER(PARTITION BY NULL) AS TOTAL_RECORD_COUNT, ROW_NUMBER() OVER (ORDER BY BOARD_GROUP DESC) AS RNUM, TB.* FROM ( SELECT LPAD(' ', 2*(LEVEL-1)) || CMNT_MENO AS CMNT_MENO , CMNT_NO, CMNT_REFNO, CMNT_LEVEL, CMNT_MENO, CMNT_USER_ID, CMNT_USER_DT, USE_YN, BOARD_SEQ, CMNT_GROUP TO_CHAR(CMNT_USER_DT, 'yyyy-MM-dd') CMNT_USER_DT FROM TSES_CMNT_INFO_I a WHERE a.BOARD_SEQ = #{params.boardSeq}
AND CMNT_MENO LIKE '%'||#{params.searchKeyword } ||'%'
START WITH CMNT_REFNO=0 CONNECT BY PRIOR CMNT_NO = CMNT_REFNO ORDER SIBLINGS BY CMNT_GROUP DESC ) TB )TA WHERE RNUM BETWEEN #{params.firstIndex} + 1 AND #{params.firstIndex} + #{params.recordCountPerPage}

choose.when:
	                 INSERT INTO TSES_CMNT_INFO_I (
		                   CMNT_NO, CMNT_REFNO, CMNT_LEVEL, CMNT_GROUP, CMNT_MENO, CMNT_USER_ID, CMNT_USER_DT, USE_YN, BOARD_SEQ                
                   )
                   VALUES (#{seq}, #{cmntRefno}, #{cmntlevel}, ${seq},  #{cmntMeno,jdbcType=VARCHAR},
		                   #{userId,jdbcType=VARCHAR}, SYSDATE, 'Y',  #{boardSeq,jdbcType=VARCHAR}                     
		                  )

choose.otherwise:
	                INSERT INTO TSES_CMNT_INFO_I (
		                   CMNT_NO, CMNT_REFNO, CMNT_LEVEL, CMNT_GROUP, CMNT_MENO, CMNT_USER_ID, CMNT_USER_DT, USE_YN, BOARD_SEQ                  
                   )
                   VALUES (#{seq},  #{cmntRefno}, #{cmntlevel}, #{cmntroup},  #{cmntMeno,jdbcType=VARCHAR},
		                   #{userId,jdbcType=VARCHAR}, SYSDATE, 'Y',  #{boardSeq,jdbcType=VARCHAR}                    
		                    )
