mapper.select:
SELECT * FROM ( SELECT SUM(1) OVER(PARTITION BY NULL) AS TOTAL_RECORD_COUNT, ROW_NUMBER() OVER (ORDER BY ROWNUM DESC) AS RNUM, TB.* FROM ( SELECT A.QR_CHECK_SEQ, A.RESV_SEQ, A.USER_ID, TO_CHAR(A.QR_CHECK_TM,'YYYY-MM-DD HH24:MI:SS') AS QR_CHECK_TM, A.INOUT_DVSN, A.QR_UPDT, TO_CHAR(A.RCV_DT,'YYYY-MM-DD HH24:MI:SS') AS RCV_DT, A.RCV_CD, B.RESV_USER_NM, CASE INOUT_DVSN WHEN 'IN' THEN '입장' ELSE '퇴장' END AS INOUT_DVSN_TEXT FROM TSER_QR_CHECK_H A, TSER_RESV_INFO_I B, TEST_USER_INFO C WHERE A.RESV_SEQ = B.RESV_SEQ AND A.USER_ID = C.USER_ID
AND A.RESV_SEQ = #{params.searchKeyword}
AND A.USER_ID LIKE '%' || #{params.searchKeyword} || '%'
) TB ) TA WHERE ROWNUM BETWEEN #{params.firstIndex} + 1 AND #{params.firstIndex} + #{params.recordCountPerPage} ORDER BY TA.QR_CHECK_TM DESC

SELECT A.QR_CHECK_SEQ, A.RESV_SEQ, B.USER_ID, A.QR_CHECK_TM, A.INOUT_DVSN, A.QR_UPDT, A.RCV_DT, A.RCV_CD, B.RESV_USER_NM FROM TSER_QR_CHECK_H A, TSER_RESV_INFO_I B, TEST_USER_INFO C WHERE A.RESV_SEQ = B.RESV_SEQ AND A.USER_ID = C.USER_ID
AND TO_CHAR(A.QR_CHECK_TM, 'yyyyMMdd') = TO_CHAR(SYSDATE, 'yyyyMMdd') AND B.RESV_START_DT = TO_CHAR(SYSDATE, 'yyyyMMdd') AND A.RESV_SEQ = #{resvSeq} AND ROWNUM = 1 ORDER BY A.QR_CHECK_TM DESC
AND A.QR_CHECK_SEQ = #{qrCheckSeq}

mapper.insert:

             SELECT USER_ID AS userId FROM TSER_RESV_INFO_I WHERE RESV_SEQ = #{resvSeq}


	    INSERT INTO TSER_QR_CHECK_H	
	    (
	    	QR_CHECK_SEQ, RESV_SEQ, USER_ID, QR_CHECK_TM, INOUT_DVSN, 
	    	QR_UPDT, RCV_DT, RCV_CD, QR_CODE 
	    )
	    VALUES


				( 
	    			QR_CK_SEQ.NEXTVAL, #{resvSeq}, #{userId}, SYSDATE, #{inoutDvsn}, 
	    			SYSDATE, SYSDATE, 'MANUAL', #{qrCode, jdbcType=VARCHAR}
				)


				( 
	    			QR_CK_SEQ.NEXTVAL, #{resvSeq}, #{userId}, SYSDATE, #{inoutDvsn}, 
	    			SYSDATE, #{rcvDt}, #{rcvCd}, #{qrCode}
				)
